        VehicleTelemetryProcessor processor = new VehicleTelemetryProcessor();
        String encodedXml = Base64.getEncoder().encodeToString("<TelemetryReading vehicleId=\"1\" latitude=\"40.7128\" longitude=\"-74.0060\"/>");
        processor.processTelemetry(encodedXml);
    }
