    private static final String ALGORITHM = "AES";
    private static final byte[] KEY_VALUE = { 'J', 'a', 'v', 'a', 'S', 'e', 'c', 'r', 'e', 't', 'K', 'e', 'y', '1', '2', '3' };
    public static void main(String[] args) {
        if (args.length != 1) {
            System.out.println("Usage: java EmergencyDispatchService <serialized_data>");
            return;
        }
        try {
            String serializedData = args[0];
            byte[] decodedBytes = Base64.getDecoder().decode(serializedData);
            byte[] decryptedData = decrypt(decodedBytes);
            ObjectInputStream ois = new ObjectInputStream(new ByteArrayInputStream(decryptedData));
            EmergencyResponse request = (EmergencyResponse) ois.readObject();
            ois.close();
            processRequest(request);
        } catch (Exception e) {
            System.err.println("Failed to deserialize or process request: " + e.getMessage());
        }
    }
    private static byte[] decrypt(byte[] data) throws Exception {
        SecretKeySpec key = new SecretKeySpec(KEY_VALUE, ALGORITHM);
        Cipher cipher = Cipher.getInstance(ALGORITHM);
        cipher.init(Cipher.DECRYPT_MODE, key);
        return cipher.doFinal(data);
    }
    private static void processRequest(EmergencyResponse request) {
        System.out.println("Processing request for emergency: " + request.getDescription());
    }
}

