public class TriageRecordsSerializer implements Serializable {
    private static final long serialVersionUID = 1L;
    private String patientName;
    private int age;
    private String medicalCondition;
    public TriageRecordsSerializer(String patientName, int age, String medicalCondition) {
        this.patientName = patientName;
        this.age = age;
        this.medicalCondition = medicalCondition;
    }
    public String getPatientName() {
        return patientName;
    }
    public void setPatientName(String patientName) {
        this.patientName = patientName;
    }
    public int getAge() {
        return age;
    }
    public void setAge(int age) {
        this.age = age;
    }
    public String getMedicalCondition() {
        return medicalCondition;
    }
    public void setMedicalCondition(String medicalCondition) {
        this.medicalCondition = medicalCondition;
    }
    private void readObject(ObjectInputStream ois) throws IOException, ClassNotFoundException {
        try {
            ois.defaultReadObject();
            if (patientName == null || patientName.isEmpty()) {
                throw new IllegalArgumentException("Patient name cannot be null or empty");
            }
            if (age < 0) {
                throw new IllegalArgumentException("Age cannot be negative");
            }
            if (medicalCondition == null || medicalCondition.isEmpty()) {
                throw new IllegalArgumentException("Medical condition cannot be null or empty");
            }
        } catch (IOException e) {
            throw new IOException("Error reading object", e);
        } catch (ClassNotFoundException e) {
            throw new ClassNotFoundException("Class not found", e);
        }
    }
    public static void main(String[] args) {
        TriageRecordsSerializer triageRecord = new TriageRecordsSerializer("John Doe", 30, "Flu");
    }
}

public class CaseFileHandler {
    private String caseId;
    private String filePath;
    public CaseFileHandler(String caseId, String filePath) {
        this.caseId = caseId;
        this.filePath = filePath;
    }
    public void loadCaseFile() {
        try (ObjectInputStream ois = new ObjectInputStream(new FileInputStream(filePath))) {
            CaseRecord record = (CaseRecord) ois.readObject();
            System.out.println("Loaded case file for: " + record.getCaseId());
        } catch (IOException | ClassNotFoundException e) {
            System.err.println("Error loading case file: " + e.getMessage());
        }
    }
    public static void main(String[] args) {
        if (args.length != 2) {
            System.err.println("Usage: java CaseFileHandler <caseId> <filePath>");
            return;
        }
        String caseId = args[0];
        String filePath = validateFilePath(args[1]);
        if (filePath != null) {
            CaseFileHandler handler = new CaseFileHandler(caseId, filePath);
            handler.loadCaseFile();
        }
    }
    private static String validateFilePath(String filePath) {
        if (filePath.contains("..") || filePath.contains("/")) {
            System.err.println("Invalid file path: " + filePath);
            return null;
        }
        String basePath = "/secure/data/casefiles/";
        return basePath + filePath;
    }
}
